import { useState } from "react";

function App() {
  const [email, setEmail] = useState(" ");
  const [message, setMessage] = useState(" ");
  function handleEmail(event) {
    let inputValue = event.target.value;
    setEmail(inputValue);

    let emailRegex = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
    if (!emailRegex.test(inputValue)) {
      setMessage("Error! you have entered invalid email.");
    } else {
      setMessage("");
    }
  }

  const [checked, setChecked] = useState(false);
  function handleChecked() {
    setChecked(!checked);
  }

  return (
    <div className="border border-black rounded-2xl m-10">
      <div>
      <h1 className="flex justify-center items-center pb-2">Subscribe to our newsletter</h1>
      <h2 className="flex justify-center items-center">Enter your email to join our newsletter.</h2>
      </div>
      <form>
        <div className="flex-grow flex justify-end">
          <input
            type="email"
            placeholder=" Enter your Email"
            value={email}
            onChange={handleEmail}
          />
          {message}
        </div>
        <div>
          {!checked
            ? "You need to check the box"
            : "you can now be a part of Alegance newsletter"}
          <input type="checkbox" onClick={handleChecked} /> I agree to Alegance
          privacy policy and Term Of Use
        </div>
      </form>
      <button>Get Now</button>
    </div>
  );
}

export default App;
